"use strict";
const binary = 0b1101; //0bがbinary（2進数）の印
const octal = 0o372; //0oがoctal（8進数）の印
const hexadecimal = 0xffff; //0xがhexadecimalの印
const big = 1e8;
const small = 1e-2;
console.log("binary:", binary);
console.log("octal:", octal);
console.log("hexadecimal:", hexadecimal);
console.log("big:", big);
console.log("small:", small);
const billion = 1000000000;
console.log(billion);
// タイプを見たい
console.log(typeof billion);
console.log(0.1 + 0.2 === 0.3);
console.log(0.1 + 0.2);
console.log(9007199254740992 + 1);
